                      if (this.state.passw !== this.state.conf_passw && this.state.conf_passw .length > 1){

                          return (
                              <div className="reg_text2" >the password need to match</div>
                          )
                      }

                      return null;
                    })()}
                </form>

              </div>
            </header>
          </div>
              );
        }
}

export default landing;
