import React  , { useState, useEffect, Fragment }  from "react";
import {Button, Container, Row, Col, Table, Form} from 'react-bootstrap'
import 'bootstrap/dist/css/bootstrap.min.css'
import axios from "axios";
import token from './token';



const Trading = () => {

    const [email, setEmail] = useState('')
    const [money, setMoney] = useState('')
    const [bearerX, setbearerX] = useState('')
    const [user, setUser] = useState('')
    const [username, setUsername] = useState('')
    const [stock_name, setStock_name] = useState('')
    const [quantity, setQuantity] = useState('')
    const [price, setPrice] = useState('')
    const [loading, setLoading] = useState(true);
    const [errors, setErrors] = useState(false);

    useEffect(() => {
      if (localStorage.getItem('token') == null) {
        window.location.replace('http://localhost:3000/');
      } else {
        setLoading(false);
      }



      var myHeaders = new Headers();
        var bearer = "bearer "
        // console.log("This is my bearer: ", bearer)
        bearer += localStorage.getItem('token')
        // console.log("This is my bearer: ", bearer)
        myHeaders.append("Authorization", bearer);
        setbearerX(bearer)

        var requestOptions = {
          method: 'GET',
          headers: myHeaders,
          redirect: 'follow'
        };

        var tomato = {
          "money": 0,
          "user__email": "",
          "user__username": "",
        };


        ////////////////////////////////
        //////////GET USER
        ///////////////////////////////

        fetch("https://stock-pipeli-users-fje2brrt8yy.herokuapp.com/getuser/", requestOptions)
        .then(response => response.json())
        .then(function(result){
          console.log("This is a result: ", result[0])
          setUsername(result[0].user__username)
          setEmail(result[0].user__email)
          setMoney(result[0].money)

         } )
        .catch(function(error){
            console.log('error', error)
            localStorage.clear()
             setErrors(true)
           });

        ////////////////////////////////
        //////////GET STOCK
        ///////////////////////////////



    }, []);




    function onSucess(e)
    {
        var myHeaders = new Headers();
        var bearer = "bearer "
        // console.log("This is my bearer: ", bearer)
        bearer += localStorage.getItem('token')
        // console.log("This is my bearer: ", bearer)
        myHeaders.append("Authorization", bearer);

        var formdata = new FormData();
        formdata.append("username", username);

        var requestOptions = {
            method: 'GET',
            headers: myHeaders,
            body: formdata,
            redirect: 'follow'
        };

        fetch("https://stock-pipeli-users-fje2brrt8yy.herokuapp.com/getstocks/", requestOptions)
        .then(response => response.json())
        .then(function(result)
        {
            console.log("This are the tock available ",result)
        })
        .catch(error => console.log('error', error));

    }

    // render()
    // {

        return(

            <div>
            {errors ==true?
                <div> You had gotten Logout, your account has expired</div>
                :
                <div> 

                    <div> here I shuu{onSucess()} </div>
                    <Container>
                        <Row>
                            <p>User Test</p>
                            <p> </p>
                            <Col><h4>Balance: $420.69</h4></Col>
                            <Col></Col>
                            <Col></Col>
                        </Row>
                        <p className="mt-5"></p>
                        <Row>
                            <Col xs={6}>
                                <h2>Your Stock List</h2>
                                <p className="mt-5"></p>
                                <Table striped hover>
                                <thread>
                                    <tr>
                                        <th>Symbol</th>
                                        <th>Name</th>
                                        <th>Price</th>
                                        <th>Quantity</th>
                                    </tr>
                                <tbody>
                                    <tr>
                                        <th>GME</th>
                                        <th>Gamestop</th>
                                        <th>$349.22</th>
                                        <th>233</th>
                                    </tr>
                                    <tr>
                                        <th>AMC</th>
                                        <th>AMC Entertainment</th>
                                        <th>$49.22</th>
                                        <th>2333</th>
                                    </tr>
                                    <tr>
                                        <th>RKT</th>
                                        <th>RKT Mortgage</th>
                                        <th>$33.22</th>
                                        <th>213</th>
                                    </tr>
                                    <tr>
                                        <th>TLRY</th>
                                        <th>Tilray</th>
                                        <th>$19.85</th>
                                        <th>42</th>
                                    </tr>
                                </tbody>
                                </thread>
                                </Table>
                            </Col>

                        </Row>
                    </Container>
            </div>
                }
            </div>


        );
    // }

}
export default Trading;
